import React from "react";
import "fontsource-vollkorn";
import { makeStyles, rgbToHex, hexToRgb } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import { Link, Route, Switch } from "react-router-dom";
import Home from "./Home";
import { Menu, MenuItem } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    fontFamily: ("PT Serif", "Georgia"),
  },
  bar: {
    backgroundColor: hexToRgb("#00356B"),
  },
  menuButton: {
    marginRight: theme.spacing(2),
    color: "white",
  },
  title: {
    flexGrow: 1,
    textAlign: "center",
    color: "white",
  },
}));

export default function ButtonAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div findAllByDisplayValue className={classes.root}>
      <nav>
        <AppBar position="static" className={classes.bar}>
          <Toolbar>
            <div>
              <IconButton
                edge="start"
                className={classes.menuButton}
                aria-label="menu"
                aria-controls="simple-menu"
                aria-haspopup="true"
                onClick={handleClick}
              >
                <MenuIcon />
              </IconButton>
              <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
              >
                <MenuItem>Home</MenuItem>
                <MenuItem>Writings</MenuItem>
                <MenuItem>What I've Done</MenuItem>
              </Menu>
            </div>
            <Typography variant="h3" className={classes.title}>
              BESSIE BAUMAN
            </Typography>
            <Button color="inherit">Contact</Button>
          </Toolbar>
        </AppBar>
      </nav>
      <Route path="/" component={Home} />
    </div>
  );
}
